$(document).ready((function(){AOS.init({disable:"mobile"}), $("[data-bs-hover-animate]").mouseenter((function(){var t=$(this);t.addClass("animated "+t.attr("data-bs-hover-animate"))})).mouseleave((function(){var t=$(this); t.removeClass("animated "+t.attr("data-bs-hover-animate"))})),$("[data-bs-tooltip]").tooltip()})),
document.querySelectorAll('a[href^="#"]').forEach(t=> {
    t.addEventListener("click",(function(t){
        t.preventDefault(),
        document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth",block:"center"})})
)});

